import React from 'react';
import InteractiveElement from './InteractiveElement';

function ColorSelector({ options }) {
  const [isPopupActive, setIsPopupActive] = React.useState(false);
  const [activeColor, setActiveColor] = React.useState(options[0].color);
  return (
    <>
      {isPopupActive && (
        <ColorPopup
          state={[isPopupActive, setIsPopupActive, activeColor, setActiveColor]}
          options={options}
        />
      )}
      <InteractiveElement
        className="color-selector"
        style={{ backgroundColor: activeColor }}
        tag="div"
        onClick={() => {
          setIsPopupActive(true);
        }}
      />
    </>
  );
}
const ColorPopup = ({ state, options }) => {
  const [isPopupActive, setIsPopupActive, activeColor, setActiveColor] = state;
  return (
    <div className="color-popup">
      {options.map(({ value, color }, index) => {
        const handleColorClick = () => {
          setActiveColor(value);
        };
        return (
          <InteractiveElement
            key={index}
            className={
              color === activeColor
                ? 'color-popup-box selected'
                : 'color-popup-box'
            }
            style={{ backgroundColor: color }}
            onClick={handleColorClick}
            tag="div"
          />
        );
      })}
    </div>
  );
};
export default ColorSelector;
